IDocumentProperty documentProperty = customDocumentProperties.Add("PropertyA");
//Set a date time.
documentProperty.DateTime = DateTime.Now;
//Save the presentation
presentation.Save("DateTime.pptx");           
//Close the presentation.
presentation.Close();

