//Set the begin arrow head length
borderBottom.BeginArrowheadLength = ArrowheadLength.Long;
//Set the dash style of border bottom
borderBottom.DashStyle = LineDashStyle.DashDotDot;
//Set the weight
borderBottom.Weight = 5;
